    @FXML
    private AnchorPane paneContainer;

    @FXML
    private ScrollPane prodiInformationPane;

    @FXML
    private ImageView imageProdi;

    @FXML
    private Label headingLabel, namaProdiLabel, nomorProdiLabel, deskripsiProdiLabel, namaJMTI, namaJSTPK, namaJTIP, namaJTSP, namaJIKL, deskripsiJMTI, deskripsiJSTPK, deskripsiJTIP, deskripsiJTSP, deskripsiJIKL;

    @FXML
    private Button backButton, badges, buttonBisnisDigital, buttonIlmuAktuaria, buttonInformatika, buttonMatematika, buttonSistemInformasi, buttonStatistika, buttonFisika, buttonTeknikKelautan, buttonTeknologiPangan, buttonTeknikPerkapalan, buttonRekayasaKeselamatan,buttonTeknikElektro, buttonTeknikIndustri, buttonTeknikKimia, buttonTeknikLogistik, buttonTeknikMesin, buttonArsitektur, buttonDesainKomunikasiVisual, buttonPerencanaanWilayahdanKota, buttonTeknikSipil, buttonTeknikLingkungan, buttonTeknikMaterialdanMetalurgi;

    @Override
    public void initialize(URL url, ResourceBundle resourceBundle) {
        initializeJurusanLabels();
        initializeProdiButton();
    }

    private void initializeJurusanLabels() {
        setJurusanLabel(namaJMTI, deskripsiJMTI, JMTI);
        setJurusanLabel(namaJSTPK, deskripsiJSTPK, JSTPK);
        setJurusanLabel(namaJTIP, deskripsiJTIP, JTIP);
        setJurusanLabel(namaJTSP, deskripsiJTSP, JTSP);
        setJurusanLabel(namaJIKL, deskripsiJIKL, JIKL);
    }

    private void setJurusanLabel(Label namaLabel, Label deskripsiLabel, JurusanModel jurusan) {
        namaLabel.setText(jurusan.getNama());
        deskripsiLabel.setText(jurusan.getDeskripsi());
    }

